import React, { Component } from 'react';
import {Col, Row,Button,Form, Modal,Input, Rate, Icon } from 'antd'; 
import './About.css'
import PropTypes from 'prop-types';
import GetViewPort from '../hoc-helpers/GetViewPort'

const FormItem = Form.Item;

// const { getFieldDecorator } = form;
const hasValidLength = value => value.length > 1 && value.length < 3;
const desc = ['SEDENTARY-limited exercise',
              'LIGHTLY ACTIVE-light exercise less than three days per week',
              'MODERATELY ACTIVE-moderate exercise most days of the week',
              'VERY ACTIVE-hard exercise every day', 
              'EXTRA ACTIVE-strenuous exercise two or more times per day' ]

const hasNumbersOnly = value => {
  const regex = /^[0-9]+$/;

  return regex.test(value);
};

const validateEntryPattern = value => {
    return {
      validLength: hasValidLength(value),
      numbersOnly: hasNumbersOnly(value),
    };
  };

  const validateForNumber = value => {
    let message = '';

    if (value !== undefined) {
      const result = validateEntryPattern(value);

      if (!result.validLength) {
        message += 'Please check the value entered';
      }
      if (!result.numbersOnly) {
        message.length > 0 ? (message += ', numbers only') : (message += 'numbers only');
      }
      if (message.length > 0) {
        message += '.';
      }
    }

    return message;
  };
class MacroCalculator extends Component{ 
state={
    years : '',
    ft : '',
    inch : '',
    weight: '',
    ratevalue: 3,
}
    validateForNumber = (value) => {
        const result = hasNumbersOnly(value);

        if (result === true) {
        //   callback(`Must contain ${result}`);
        return true;
        }

        // callback();
        return false;
      };

      componentDidMount() {
          const {
    form,
  } = this.props;

        console.log(this.props);
    }

    handleChange = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                years: e.target.value
            })
        }
        else{
            this.setState({
                years:""
            })
        }
    }
    handleChange1 = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                weight: e.target.value
            })
        }
        else{
            this.setState({
                weight:""
            })
        }
    }
    handleChange2 = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                ft: e.target.value
            })
        }
        else{
            this.setState({
                ft:""
            })
        }
    }
    handleChange3 = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                inch: e.target.value
            })
        }
        else{
            this.setState({
                inch:""
            })
        }
    }
    handelratevalue = (value) =>{
        console.log(value)
        this.setState({
            ratevalue: value

        })

    }

    render() { 
     const { width } = this.props; 
     const {ratevalue} = this.state;
    if (width < 786) { 

    return( 

    <div className="page"> 

    </div> 

    ); 

    } 


    return( 

    <div className="page"> 
        <div className="form">
            <Col span={12}>
                <Form>
                    <FormItem label="Macro Calculator">
                        <Input addonBefore='Age:' id='years' value={this.state.years} addonAfter="Yrs" allowClear placeholder="Enter your age here" onChange={this.handleChange}/>
                    </FormItem>
                    <FormItem>
                        <Input addonBefore='Weight:' id='lbs' value={this.state.weight} addonAfter="lbs" allowClear placeholder="Enter your weight here" onChange={this.handleChange1}/>
                    </FormItem>
                    <FormItem>
                    <Col span={12}>
                        <Input addonBefore='Height:' id='ft' value={this.state.ft} addonAfter="Ft" allowClear placeholder="Enter your height here" onChange={this.handleChange2}/>
                    </Col>
                    <Col span={12}>
                        <Input  id='inch' value={this.state.inch} addonAfter="inch" allowClear placeholder="Enter your height here" onChange={this.handleChange3}/>
                    </Col>
                    <FormItem >
                            <Rate label="Select Exercise level" tooltips={desc} onChange={this.handelratevalue} value={this.state.ratevalue} character={<Icon type="skin" />} style={{ fontSize: 34 }}/>
                    </FormItem>

                    </FormItem>
         </Form>
         </Col>
         </div>
    </div> 

    ); 

    } 

    }; 

    MacroCalculator.propTypes = { 

    width: PropTypes.object, 

    }; 

    const MacroCalculatorWithHOC = GetViewPort(MacroCalculator) 

    export default MacroCalculatorWithHOC; 

    {/* </Col>
            <Col>
                Weight:
                <Input addonAfter="lbs" allowClear placeholder="Enter your weight in lbs here" />
            </Col>
            <Col>
                Height:
                <Col>
                    <Col span={12}><Input addonAfter="Ft" allowClear placeholder="Enter your Height in ft here" /></Col>
                    <Col span={12}><Input addonAfter="in" allowClear placeholder="Enter your Height in inches here" /></Col>
                </Col>
            </Col>
         </Col>
         <Col span={12}>Image</Col> */}
