import React, { Component } from 'react';
import {Col, Row,Button,Form, Modal,Input } from 'antd'; 
import './About.css'
import PropTypes from 'prop-types';
import GetViewPort from '../hoc-helpers/GetViewPort'

const FormItem = Form.Item;

// const { getFieldDecorator } = form;
const hasValidLength = value => value.length > 1 && value.length < 3;

const hasNumbersOnly = value => {
  const regex = /^[0-9]+$/;

  return regex.test(value);
};

const validateEntryPattern = value => {
    return {
      validLength: hasValidLength(value),
      numbersOnly: hasNumbersOnly(value),
    };
  };

  const validateForNumber = value => {
    let message = '';

    if (value !== undefined) {
      const result = validateEntryPattern(value);

      if (!result.validLength) {
        message += 'Please check the value entered';
      }
      if (!result.numbersOnly) {
        message.length > 0 ? (message += ', numbers only') : (message += 'numbers only');
      }
      if (message.length > 0) {
        message += '.';
      }
    }

    return message;
  };
class MacroCalculator extends Component{ 
state={
    years : '',
    weight: ''
}
    validateForNumber = (value) => {
        const result = hasNumbersOnly(value);

        if (result === true) {
        //   callback(`Must contain ${result}`);
        return true;
        }

        // callback();
        return false;
      };

      componentDidMount() {
          const {
    form,
  } = this.props;

        console.log(this.props);
    }

    handleChange = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                years: e.target.value
            })
        }
        else{
            this.setState({
                years:""
            })
        }
    }
    handleChange1 = (e) => {
        const isValid = this.validateForNumber(e.target.value)
        console.log(isValid)
        if (isValid) {
            this.setState({
                weight: e.target.value
            })
        }
        else{
            this.setState({
                weight:""
            })
        }
    }

    render() { 
     const { width } = this.props; 

    if (width < 786) { 

    return( 

    <div className="page"> 

    </div> 

    ); 

    } 


    return( 

    <div className="page"> 
        <div className="form">
            <Col span={12}>
                <Form>
                    <FormItem label="Plain Text">
                        <Input id='years' value={this.state.years} addonAfter="Yrs" allowClear placeholder="Enter your age here" onChange={this.handleChange}/>
                    </FormItem>
                    <FormItem>
                        <Input id='lbs' value={this.state.weight} addonAfter="lbs" allowClear placeholder="Enter your age here" onChange={this.handleChange1}/>
                    </FormItem>
         </Form>
         </Col>
         </div>
    </div> 

    ); 

    } 

    }; 

    MacroCalculator.propTypes = { 

    width: PropTypes.object, 

    }; 

    const MacroCalculatorWithHOC = GetViewPort(MacroCalculator) 

    export default MacroCalculatorWithHOC; 

